import React from 'react';
import { ActivityIndicator, Platform , Button , StatusBar, StyleSheet , Text, View, TextInput, TouchableOpacity, Image, ScrollView } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { AntDesign, Entypo, Octicons, FontAwesome, MaterialCommunityIcons } from "@expo/vector-icons";
import HomeScreen from "./Screens/HomeScreen"
import LoginScreen from "./Screens/LoginScreen"
import LocalStorage from "./Screens/LocalStorage";
import ProfileScreen from "./Screens/ProfileScreen";

const Tab = createBottomTabNavigator();

export default class App extends React.Component {

  constructor(){
    super();
    this.state={
      User:[],
      isLogged:"false"
    }
    this.setUser=this.setUser.bind(this);
    // this.removeUser=this.removeUser.bind(this);
  }

  componentWillMount=()=>{
    LocalStorage.getItem("isLogged",(value)=>{
      LocalStorage.getItem("User",(data)=>{
        this.setState({isLogged:value,User:data})
      })
    })
  }

  setUser=(User)=>{
    this.setState({isLogged:"true",User:User})
  }

  removeUser=()=>{
    this.setState({isLogged:"false"});
  }

  render() {
    console.disableYellowBox = true; 
    return (
     	<View style={{ paddingTop: Platform.OS === 'android' ? StatusBar.currentHeight : 0,height:"100%",backgroundColor:"black" }}>
     	  <NavigationContainer>
          <Tab.Navigator>
            <Tab.Screen 
              name="Home"
              component={HomeScreen} 
              options={{
                tabBarLabel: 'Home',
                tabBarIcon: ({ color, size }) => (
                  <Entypo name="home" size={24} color="black" />
                ),
              }}
            />
            {
              this.state.isLogged=="true"?
              <Tab.Screen 
                name="Profile" 
                children={()=><ProfileScreen User={this.state.User}  removeUser={this.removeUser.bind(this)}/>}
                options={{
                  tabBarLabel: 'Profile',
                  tabBarIcon: ({ color, size }) => (
                    <FontAwesome name="user" size={24} color="black" />
                  ),
                }}
              />
              :
              <Tab.Screen 
                name="Login" 
                children={()=><LoginScreen setUser={this.setUser.bind(this)}/>}
                options={{
                  tabBarLabel: 'Login',
                  tabBarIcon: ({ color, size }) => (
                    <MaterialCommunityIcons name="login" size={24} color="black" />
                  ),
                }}
              />
            }

          </Tab.Navigator>
        </NavigationContainer>
     	</View>
    );
  } 
}
